{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"},"kaggle":{"accelerator":"none","dataSources":[],"dockerImageVersionId":31040,"isInternetEnabled":true,"language":"python","sourceType":"script","isGpuEnabled":false}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code]\n# /// script\n# dependencies = [\n#   \"GitPython\",\n#    \"embed\",\n#    \"torch\",\n#    \"transformers>=4.21.0,<4.45.0\",\n#    \"peft\",\n#    \"pymilvus\",\n#    \"numpy\",\n# ]\n# ///\n\n# Imports\nfrom embed import BatchedInference\nfrom concurrent.futures import Future\nimport os\nimport time\nfrom pathlib import Path\n\n# Huggingface authentication\nfrom huggingface_hub import login\nlogin(token=os.getenv(\"HF_TOKEN\"))\n\n# Define model configurations with their expected dimensions\nMODEL_CONFIGS = {\n    \"jinaai/jina-embeddings-v2-base-code\": {\"dimension\": 768, \"id\": \"jina\"},\n    \"Salesforce/codet5p-110m-embedding\": {\"dimension\": 256, \"id\": \"codet5p\"},\n    \"codesage/codesage-large-v2\": {\"dimension\": 1024, \"id\": \"codesage\"}\n}\n\nprint(\"Initializing embedding models...\")\n\n# Define custom embedding function\nregister = BatchedInference(\n    model_id=list(MODEL_CONFIGS.keys()),\n    engine=\"torch\",\n    device=\"cuda\",\n)\n\nprint(\"Models loaded successfully!\")\n\n# Milvus setup\nfrom pymilvus import MilvusClient\nmilvus_client = MilvusClient(uri=\"./embeddings.db\")\n\n# Test embedding to verify dimensions\nprint(\"Testing embedding dimensions...\")\ntest_embeddings = {}\nfor model_name in MODEL_CONFIGS.keys():\n    try:\n        future = register.embed(sentences=[\"test code sample\"], model_id=model_name)\n        result = future.result()\n        actual_dim = len(result[0])\n        test_embeddings[model_name] = actual_dim\n        print(f\"{model_name}: dimension = {actual_dim}\")\n        \n        # Update configuration with actual dimension\n        MODEL_CONFIGS[model_name][\"dimension\"] = actual_dim\n    except Exception as e:\n        print(f\"Error testing {model_name}: {e}\")\n        continue\n\n# Define collection mappings\nCOLLECTION_MAPPING = {\n    \"PriA\": (\"primary\", \"jinaai/jina-embeddings-v2-base-code\"),\n    \"PriB\": (\"primary\", \"Salesforce/codet5p-110m-embedding\"),\n    \"PriC\": (\"primary\", \"codesage/codesage-large-v2\"),\n    \"FrkA\": (\"fork\", \"jinaai/jina-embeddings-v2-base-code\"),\n    \"FrkB\": (\"fork\", \"Salesforce/codet5p-110m-embedding\"),\n    \"FrkC\": (\"fork\", \"codesage/codesage-large-v2\")\n}\n\n# Drop existing collections and create new ones with correct dimensions\nprint(\"Setting up Milvus collections...\")\nfor collection_name, (repo_type, model_name) in COLLECTION_MAPPING.items():\n    if milvus_client.has_collection(collection_name):\n        milvus_client.drop_collection(collection_name)\n        print(f\"Dropped existing collection: {collection_name}\")\n    \n    # Create collection with proper dimension\n    dimension = MODEL_CONFIGS[model_name][\"dimension\"]\n    milvus_client.create_collection(\n        collection_name=collection_name,\n        dimension=dimension,\n        metric_type=\"COSINE\",\n        consistency_level=\"Strong\",\n    )\n    print(f\"Created collection {collection_name} with dimension {dimension}\")\n\ndef load(dir_path, db, model):\n    \"\"\"\n    Load repository files, embed their contents, and store in Milvus\n    \"\"\"\n    # Define code file extensions to process\n    code_extensions = {'.py', '.js', '.java', '.cpp', '.c', '.h', '.cs', '.php', '.rb', '.go', '.rs', '.swift', '.kt'}\n    \n    dir_path = Path(dir_path)\n    files_processed = 0\n    \n    # Walk through directory\n    for file_path in dir_path.rglob('*'):\n        if file_path.is_file() and file_path.suffix.lower() in code_extensions:\n            try:\n                # Read file content\n                with open(file_path, 'r', encoding='utf-8', errors='ignore') as f:\n                    content = f.read()\n                \n                # Skip empty files\n                if not content.strip():\n                    continue\n                \n                print(f\"Processing: {file_path}\")\n                \n                # Embed the file content\n                future = register.embed(sentences=[content], model_id=model)\n                embeddings = future.result()\n                \n                # Prepare data for insertion\n                data_to_insert = []\n                for i, embedding in enumerate(embeddings):\n                    data_to_insert.append({\n                        \"id\": files_processed + i,\n                        \"vector\": embedding,\n                        \"file_path\": str(file_path.relative_to(dir_path)),\n                        \"file_name\": file_path.name,\n                        \"content\": content  # Store file contents as metadata\n                    })\n                \n                # Insert into Milvus\n                milvus_client.insert(collection_name=db, data=data_to_insert)\n                files_processed += len(embeddings)\n                \n                # Small delay to prevent overwhelming the system\n                time.sleep(0.1)\n                \n            except Exception as e:\n                print(f\"Error processing {file_path}: {e}\")\n                continue\n    \n    print(f\"Processed {files_processed} files for {db}\")\n\n# Pull down repositories\nfrom git import Repo\n\ntry:\n    # Create data directory\n    os.makedirs(\"./data\", exist_ok=True)\n    \n    # Clone repositories (fixed syntax)\n    print(\"Cloning primary repository...\")\n    if os.path.exists(\"./data/primary\"):\n        import shutil\n        shutil.rmtree(\"./data/primary\")\n    primary = Repo.clone_from(\"https://github.com/cowsay-org/cowsay\", \"./data/primary\")\n    \n    print(\"Cloning fork repository...\")\n    if os.path.exists(\"./data/fork\"):\n        import shutil\n        shutil.rmtree(\"./data/fork\")\n    fork = Repo.clone_from(\"https://github.com/cowsay-org/homebrew-cowsay\", \"./data/fork\")\n    \n    # Process repositories with different models\n    print(\"Starting embedding process...\")\n    \n    # Process primary repo with different models\n    load(\"./data/primary\", \"PriA\", \"jinaai/jina-embeddings-v2-base-code\")\n    load(\"./data/primary\", \"PriB\", \"Salesforce/codet5p-110m-embedding\")\n    load(\"./data/primary\", \"PriC\", \"codesage/codesage-large-v2\")\n    \n    # Process fork repo with different models\n    load(\"./data/fork\", \"FrkA\", \"jinaai/jina-embeddings-v2-base-code\")\n    load(\"./data/fork\", \"FrkB\", \"Salesforce/codet5p-110m-embedding\")\n    load(\"./data/fork\", \"FrkC\", \"codesage/codesage-large-v2\")\n    \n    print(\"All embedding operations completed successfully!\")\n    \nexcept Exception as e:\n    print(f\"Error during execution: {e}\")\n    \nfinally:\n    # Properly shutdown the embedding service\n    print(\"Shutting down embedding service...\")\n    register.stop()\n    print(\"Script completed.\")","metadata":{"_uuid":"bc81d846-9624-4309-b47e-0f964e319df7","_cell_guid":"06033afe-71cd-4bf2-9a38-241edae31bda","trusted":true,"collapsed":false,"jupyter":{"outputs_hidden":false}},"outputs":[],"execution_count":null}]}